<?php

$inputs = ["ULL",
"RRDDD",
"LURDL",
"UUUUD"
]; // 1985

$inputs = ["",
"",
"",
"",
""];

$keypad = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
];

$line = 1;
$col = 1;

$code ="";

foreach ($inputs as $input) {
    $directions = str_split($input);

    foreach ($directions as $direction) {
        switch ($direction) {
            case "U":
                if ($line !== 0) {
                    $line--;
                }
                break;
            case "D":
                if ($line !== 2) {
                    $line++;
                }
                break;
            case "L":
                if ($col !== 0) {
                    $col--;
                }
                break;
            case "R":
                if ($col !== 2) {
                    $col++;
                }
                break;
        }
    }

    $code .= $keypad[$line][$col];
}

var_dump($code);
