<?php

$inputs = ["ULL",
"RRDDD",
"LURDL",
"UUUUD"
]; // 5DB3

$inputs = ["",
"",
"",
"",
""];

$keypad = [
    [0, 0, 1, 0, 0],
    [0, 2, 3, 4, 0],
    [5, 6, 7, 8, 9],
    [0, "A", "B", "C", 0],
    [0, 0, "D", 0, 0]
];

$line = 3;
$col = 1;

$code = "";

function isValidPosition($position, $varName)
{
    global $keypad, $line, $col;
    $_line = $line;
    $_col = $col;

    if ($position < 0 || $position > 4)
        return false;

    ${"_$varName"} = $position;
    if ($keypad[$_line][$_col] === 0)
        return false;

    return true;   
}

foreach ($inputs as $input) {
    $directions = str_split($input);

    foreach ($directions as $direction) {
        switch ($direction) {
            case "U":
                if (isValidPosition($line-1, "line")) {
                    $line--;
                }
                break;
            case "D":
                if (isValidPosition($line+1, "line")) {
                    $line++;
                }
                break;
            case "L":
                if (isValidPosition($col-1, "col")) {
                    $col--;
                }
                break;
            case "R":
                if (isValidPosition($col+1, "col")) {
                    $col++;
                }
                break;
        }
    }

    $code .= $keypad[$line][$col];
}

var_dump($code);
